// Function to dynamically load a component into a specific div
function loadComponent(id, file) {
    fetch(file)
        .then(response => {
            if (!response.ok) {
                throw new Error(`Failed to load ${file}`);
            }
            return response.text();
        })
        .then(data => {
            document.getElementById(id).innerHTML = data;
        })
        .catch(error => console.error("Error loading component:", error));
}

// Function to load the common sections on all pages
function loadCommonSections() {
    loadComponent("navbar", "components/navbar.html");
    loadComponent("header", "components/header.html");
    loadComponent("footer", "components/footer.html");
}

// Function to load the page-specific content
function loadPageContent() {
    const page = document.body.getAttribute("data-page"); // Get the page identifier
    if (page) {
        loadComponent("content", `components/${page}_section.html`);
    }
}

// Run these functions when the page loads
window.onload = function () {
    loadCommonSections();
    loadPageContent();
};

