/* Reset and Basic Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Arial', sans-serif;
}

/* General Layout */
body {
    background-color: #121212;
    color: #f1f1f1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    text-align: center;
}

/* Navbar */
#navbar {
    width: 100%;
    background-color: #1f1f1f;
    padding: 15px 0;
    position: fixed;
    top: 0;
    left: 0;
    text-align: center;
}

#navbar a {
    color: #f1f1f1;
    text-decoration: none;
    margin: 0 15px;
    font-size: 18px;
}

#navbar a:hover {
    color: #ff9900;
}

/* Header */
#header {
    margin-top: 60px;
    font-size: 24px;
    font-weight: bold;
    padding: 20px;
}

/* Main Content */
#content {
    max-width: 800px;
    padding: 20px;
    margin-top: 20px;
}

/* Footer */
#footer {
    width: 100%;
    background-color: #1f1f1f;
    padding: 15px;
    position: fixed;
    bottom: 0;
    text-align: center;
}

#footer p {
    font-size: 14px;
    color: #bbb;
}

/* Buttons */
.button {
    display: inline-block;
    padding: 10px 20px;
    background-color: #ff9900;
    color: #fff;
    text-decoration: none;
    border-radius: 5px;
    margin: 10px;
}

.button:hover {
    background-color: #e68a00;
}
